mapper.update:
UPDATE RESERVATION                  
SET r_acceptStatus = 'accepted'
WHERE dr_idx = #{drIdx} AND p_idx = #{pIdx}

UPDATE RESERVATION                  
SET r_acceptStatus = 'refused'
WHERE dr_idx = #{drIdx} AND p_idx = #{pIdx}

UPDATE RESERVATION                  
SET r_confirm = 'true'
WHERE dr_idx = #{drIdx}
