mapper.select:
SELECT * FROM passenger
WHERE p_user_name = #{pUserName}
AND p_user_email = #{pUserEmail}

SELECT * FROM passenger
WHERE p_user_name = #{pUserName}
AND p_user_tel = #{pUserTel}

SELECT * FROM passenger
WHERE p_user_name = #{pUserName}
AND p_id_num = #{p_idNum}

mapper.update:
UPDATE PASSENGER 
SET p_user_pw = #{pUserPw},
	p_user_tel = #{pUserTel}
WHERE  p_user_email = #{pUserEmail}
