mapper.update:
UPDATE DRIVER
SET d_point = d_point + #{point}
WHERE d_idx = #{dIdx}

UPDATE D_REGISTRATION
SET d_reservation_status = 'waiting'
WHERE dr_idx = #{drIdx}

UPDATE RESERVATION
SET r_cancel_status = 'canceled'
WHERE dr_idx = #{drIdx} 
AND p_idx = #{pIdx} 
AND r_cancel_status = 'uncanceled'
