mapper.select:
SELECT d_idx
FROM DRIVER
WHERE d_user_gender = #{pHopeGender}

SELECT p_user_gender
FROM PASSENGER
WHERE p_idx = #{p_idx}

mapper.update:
UPDATE D_REGISTRATION
SET d_reservation_status = 'reservated'
WHERE dr_idx = #{drIdx}

mapper.select:
SELECT r_idx
FROM RESERVATION
WHERE dr_idx = #{drIdx}
AND p_idx = #{pIdx}
AND r_cancel_status = 'uncanceled'
